import React from "react";
import "./App.css";
import { LineChart } from "react-d3-components";
import mondaySdk from "monday-sdk-js";
import { Card } from '@material-ui/core';

const monday = mondaySdk();
let data = [
    {
        label: 'Jenny',
        values: [{ x: 0, y: 2 }, { x: 1.3, y: 5 }, { x: 3, y: 6 }, { x: 3.5, y: 6.5 }, { x: 4, y: 6 }, { x: 4.5, y: 6 }, { x: 5, y: 7 }, { x: 5.5, y: 8 }]
    },
    {
        label: 'somethingB',
        values: [{ x: 0, y: 3 }, { x: 1.3, y: 4 }, { x: 3, y: 7 }, { x: 3.5, y: 8 }, { x: 4, y: 7 }, { x: 4.5, y: 7 }, { x: 5, y: 7.8 }, { x: 5.5, y: 9 }]
    }
];

const useStyles = makeStyles({
    root: {
        minWidth: 275,
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },
    title: {
        fontSize: 14,
    },
    pos: {
        marginBottom: 12,
    },
});

const classes = useStyles();

class App extends React.Component {
    
    constructor(props) {
        super(props);

        // Default state
        this.state = {
            settings: {},
            name: "",
            
        };
    }

    randomData = (e) => {
        e.preventDefault();
        this.setState((prevState) => {
            const data = prevState.data.map(d => ({
                name: d.name,
                value: Math.floor((Math.random() * 100) + 1)
            }))
            return {
                data
            }
        })
    }

    componentDidMount() {
        monday.listen("settings", res => {
            this.setState({ settings: res.data });
        });

        monday.listen("context", res => {
            this.setState({ context: res.data });
            console.log(res.data);
            monday.api(`query ($boardIds: [Int]) { boards (ids:$boardIds) { name items(limit:1) { name column_values { title text } } } }`,
                { variables: { boardIds: this.state.context.boardIds } }
            )
                .then(res => {
                    this.setState({ boardData: res.data });
                });
        })

        // {JSON.stringify(this.state.boardData, null, 2)}
    } 
 

   returnLink()
   {
       

   }

    render() {
        return (
            <div style={{ background: (this.state.settings.background) }}>
                <Card className={classes.root}>
                    <h1>
                        Price Fluxuations through Time
                    </h1>
                    <LineChart
                        data={data}
                        width={400}
                        height={400}
                        margin={{ top: 10, bottom: 50, left: 50, right: 10 }}
                    />
                </Card>

            </div>

        );
    }
}

export default App;
