import React from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
const monday = mondaySdk();
import define from "LineChart.js";

let data = {
    points: [
        [{ x: 0, y: 20 }, { x: 1, y: 30 }, { x: 2, y: 10 }, { x: 3, y: 5 },
        { x: 4, y: 8 }, { x: 5, y: 15 }, { x: 6, y: 10 }],
        [{ x: 0, y: 8 }, { x: 1, y: 5 }, { x: 2, y: 20 }, { x: 3, y: 12 },
        { x: 4, y: 4 }, { x: 5, y: 6 }, { x: 6, y: 2 }],
        [{ x: 0, y: 0 }, { x: 1, y: 5 }, { x: 2, y: 8 }, { x: 3, y: 2 },
        { x: 4, y: 6 }, { x: 5, y: 4 }, { x: 6, y: 2 }]
    ],
    xValues: [0, 1, 2, 3, 4, 5, 6],
    yMin: 0,
    yMax: 30
};

class App extends React.Component {

    constructor(props) {
        super(props);

        // Default state
        this.state = {
            settings: {},
            name: "",
            listitems: ["List Item 1", "List Item 2", "List Item 3"],
            
        };
    }

    randomData = (e) => {
        e.preventDefault();
        this.setState((prevState) => {
            const data = prevState.data.map(d => ({
                name: d.name,
                value: Math.floor((Math.random() * 100) + 1)
            }))
            return {
                data
            }
        })
    }

    componentDidMount() {
        monday.listen("settings", res => {
            this.setState({ settings: res.data });
        });

        monday.listen("context", res => {
            this.setState({ context: res.data });
            console.log(res.data);
            monday.api(`query ($boardIds: [Int]) { boards (ids:$boardIds) { name items(limit:1) { name column_values { title text } } } }`,
                { variables: { boardIds: this.state.context.boardIds } }
            )
                .then(res => {
                    this.setState({ boardData: res.data });
                });
        })

        // {JSON.stringify(this.state.boardData, null, 2)}
    } 
 

   returnLink()
   {
       //return <form><input type="button" value="Redirect Me" onclick="Redirect" /></form>;
       return "i";
   }

    render() {
        return (
            <div style={{ background: (this.state.settings.background) }}>
               
            </div>

        );
    }
}

