import React from "react";
import "./App.css";
import { LineChart } from "react-d3-components";
import mondaySdk from "monday-sdk-js";
import { Card } from '@material-ui/core';
import { ResponsiveLine } from '@nivo/line'

const monday = mondaySdk();

let data = [
    {
        "id": "norway",
        "color": "hsl(342, 70%, 50%)",
        "data": [
            {
                "x": "plane",
                "y": 138
            },
            {
                "x": "helicopter",
                "y": 187
            },
            {
                "x": "boat",
                "y": 122
            },
            {
                "x": "train",
                "y": 199
            },
            {
                "x": "subway",
                "y": 172
            },
            {
                "x": "bus",
                "y": 144
            },
            {
                "x": "car",
                "y": 187
            },
            {
                "x": "moto",
                "y": 150
            },
            {
                "x": "bicycle",
                "y": 7
            },
            {
                "x": "horse",
                "y": 102
            },
            {
                "x": "skateboard",
                "y": 23
            },
            {
                "x": "others",
                "y": 109
            }
        ]
    }
];

let data1 = [
    {
        label: 'date',
        values: [{ x: 0, y: 2 }, { x: 1.3, y: 5 }, { x: 3, y: 6 }, { x: 3.5, y: 6.5 }, { x: 4, y: 6 }, { x: 4.5, y: 6 }, { x: 5, y: 7 }, { x: 5.5, y: 8 }]
    },
    
];
var tooltipScatter = function (x, y) {
    return "x: " + x + " y: " + y;
};

var variable = "";

monday.api('query { boards(ids: [878537780]) { items { name group { id } column_values { id value text }  } }}').then(res => { variable = res; });
console.log(variable);

class App extends React.Component {
    
    constructor(props) {
        super(props);

        // Default state
        this.state = {
            settings: {},
            name: "",
            
        };
    }

    randomData = (e) => {
        e.preventDefault();
        this.setState((prevState) => {
            const data = prevState.data.map(d => ({
                name: d.name,
                value: Math.floor((Math.random() * 100) + 1)
            }))
            return {
                data
            }
        })
    }

    componentDidMount() {
        monday.listen("settings", res => {
            this.setState({ settings: res.data });
        });

        monday.listen("context", res => {
            this.setState({ context: res.data });
            console.log(res.data);
            monday.api(`query ($boardIds: [Int]) { boards (ids:$boardIds) { name items(limit:1) { name column_values { title text } } } }`,
                { variables: { boardIds: this.state.context.boardIds } }
            )
                .then(res => {
                    this.setState({ boardData: res.data });
                });
        })
    } 

    render() {
        return (
            <div style={{ background: (this.state.settings.background), paddingLeft: 100, paddingTop: 100 }}>
                {JSON.stringify(variable)}
                <Card style={{ width: 500, float: "left" }} variant="outlined">
                    <h1 style={{ paddingLeft: 10 }}>
                        Price Fluxuations over Time
                    </h1>
                    <LineChart
                        data={data1}
                        width={300}
                        height={300}
                        margin={{ top: 10, bottom: 50, left: 50, right: 10 }}
                        tooltipHtml={tooltipScatter}
                        xAxis={{ label: "time in days" }}
                        yAxis={{ label: "hundreds of dollars" }}
                    />
                </Card>

                <Card style={{ width: 500, float: "left" }} variant="outlined">
                    <h1 style={{ paddingLeft: 10 }}>
                        Price Fluxuations over Time
                    </h1>
                    <ResponsiveLine
                        data={data}
                        margin={{ top: 50, right: 110, bottom: 50, left: 60 }}
                        xScale={{ type: 'point' }}
                        yScale={{ type: 'linear', min: 'auto', max: 'auto', stacked: true, reverse: false }}
                        yFormat=" >-.2f"
                        axisTop={null}
                        axisRight={null}
                        axisBottom={{
                            orient: 'bottom',
                            tickSize: 5,
                            tickPadding: 5,
                            tickRotation: 0,
                            legend: 'transportation',
                            legendOffset: 36,
                            legendPosition: 'middle'
                        }}
                        axisLeft={{
                            orient: 'left',
                            tickSize: 5,
                            tickPadding: 5,
                            tickRotation: 0,
                            legend: 'count',
                            legendOffset: -40,
                            legendPosition: 'middle'
                        }}
                        pointSize={10}
                        pointColor={{ theme: 'background' }}
                        pointBorderWidth={2}
                        pointBorderColor={{ from: 'serieColor' }}
                        pointLabelYOffset={-12}
                        useMesh={true}
                        legends={[
                            {
                                anchor: 'bottom-right',
                                direction: 'column',
                                justify: false,
                                translateX: 100,
                                translateY: 0,
                                itemsSpacing: 0,
                                itemDirection: 'left-to-right',
                                itemWidth: 80,
                                itemHeight: 20,
                                itemOpacity: 0.75,
                                symbolSize: 12,
                                symbolShape: 'circle',
                                symbolBorderColor: 'rgba(0, 0, 0, .5)',
                                effects: [
                                    {
                                        on: 'hover',
                                        style: {
                                            itemBackground: 'rgba(0, 0, 0, .03)',
                                            itemOpacity: 1
                                        }
                                    }
                                ]
                            }
                        ]}
                    />
                </Card>

                
            </div>

        );
    }
}

export default App;
